def getWordVecs(text):
    wvec, vecs = [], []
    s = text.split()
    for word in s:
        try:
            vecs.append(model[word].reshape((1,300)))
        except KeyError:
            vecs.append(model['qwerty'].reshape((1,300)))
        wvec.append(word)
    vecs = np.mean(np.concatenate(vecs), axis=0)
    return vecs

