def cossim(vec, moviemat):
    cos, col = [], []
    columns = moviemat.columns
    for i in range(0, moviemat.shape[1]):
        set1 = set(vec.dropna().index)
        set2 = set(moviemat[columns[i]].dropna().index)
        if len(set1 & set2) > 5:
            numer = np.sum(vec*moviemat[columns[i]])
            denom_1 = np.sqrt(np.sum(vec*vec))
            denom_2 = np.sqrt(np.sum(moviemat[columns[i]]*moviemat[columns[i]]))
            cos.append(numer/(denom_1*denom_2))
            col.append(columns[i])
    return pd.DataFrame({'userid': col, 'cos_sim': cos})
