def fillage(cols):
    Age = cols[0]
    Type = cols[1]

    if pd.isnull(Age):
        if Type == 'Dog':
            return df.loc[df['AnimalType'] == 'Dog', 'AgeuponOutcome'].median()
        elif Type == 'Cat':
            return df.loc[df['AnimalType'] == 'Cat', 'AgeuponOutcome'].median()
    else:
        return Age

df['AgeuponOutcome'] = df[['AgeuponOutcome','AnimalType']].apply(fillage, axis=1)

